import React, { useState } from "react";
import { Table, Button, Form, Modal } from "react-bootstrap";

const Products = () => {
    const [products, setProducts] = useState([]); // Estado de los productos
    const [showModal, setShowModal] = useState(false);
    const [editingProduct, setEditingProduct] = useState(null);
    const [formData, setFormData] = useState({
        name: "",
        description: "",
        price: "",
        category: "",
        stock: ""
    });

    const handleInputChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (editingProduct !== null) {
            setProducts(products.map((product, index) => (index === editingProduct ? formData : product)));
        } else {
            setProducts([...products, formData]);
        }
        setShowModal(false);
        setEditingProduct(null);
        setFormData({ name: "", description: "", price: "", category: "", stock: "" });
    };

    const handleEdit = (index) => {
        setEditingProduct(index);
        setFormData(products[index]);
        setShowModal(true);
    };

    const handleDelete = (index) => {
        setProducts(products.filter((_, i) => i !== index));
    };

    return (
        <div className="container mt-5">
            <h2 className="text-center mb-4">Gestión de Productos</h2>
            <Button variant="primary" onClick={() => setShowModal(true)}>Agregar Producto</Button>
            <Table striped bordered hover className="mt-3">
                <thead>
                    <tr>
                        <th>Nombre</th>
                        <th>Descripción</th>
                        <th>Precio</th>
                        <th>Categoría</th>
                        <th>Cantidad</th>
                        <th>Acciones</th>
                    </tr>
                </thead>
                <tbody>
                    {products.map((product, index) => (
                        <tr key={index}>
                            <td>{product.name}</td>
                            <td>{product.description}</td>
                            <td>${product.price}</td>
                            <td>{product.category}</td>
                            <td>{product.stock}</td>
                            <td>
                                <Button variant="warning" onClick={() => handleEdit(index)} className="me-2">Editar</Button>
                                <Button variant="danger" onClick={() => handleDelete(index)}>Eliminar</Button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </Table>

            <Modal show={showModal} onHide={() => setShowModal(false)}>
                <Modal.Header closeButton>
                    <Modal.Title>{editingProduct !== null ? "Editar Producto" : "Agregar Producto"}</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form onSubmit={handleSubmit}>
                        <Form.Group className="mb-3">
                            <Form.Label>Nombre</Form.Label>
                            <Form.Control
                                type="text"
                                name="name"
                                value={formData.name}
                                onChange={handleInputChange}
                                required
                            />
                        </Form.Group>
                        <Form.Group className="mb-3">
                            <Form.Label>Descripción</Form.Label>
                            <Form.Control
                                type="text"
                                name="description"
                                value={formData.description}
                                onChange={handleInputChange}
                                required
                            />
                        </Form.Group>
                        <Form.Group className="mb-3">
                            <Form.Label>Precio</Form.Label>
                            <Form.Control
                                type="number"
                                name="price"
                                value={formData.price}
                                onChange={handleInputChange}
                                required
                            />
                        </Form.Group>
                        <Form.Group className="mb-3">
                            <Form.Label>Categoría</Form.Label>
                            <Form.Control
                                type="text"
                                name="category"
                                value={formData.category}
                                onChange={handleInputChange}
                                required
                            />
                        </Form.Group>
                        <Form.Group className="mb-3">
                            <Form.Label>Cantidad en Inventario</Form.Label>
                            <Form.Control
                                type="number"
                                name="stock"
                                value={formData.stock}
                                onChange={handleInputChange}
                                required
                            />
                        </Form.Group>
                        <Button variant="primary" type="submit">{editingProduct !== null ? "Actualizar" : "Agregar"}</Button>
                    </Form>
                </Modal.Body>
            </Modal>
        </div>
    );
};

export default Products;
