import React from 'react';
import { useNavigate } from 'react-router-dom'; // Importamos useNavigate

const Header = () => {
    const navigate = useNavigate(); // Definimos navigate correctamente

    const handleHomeClick = () => {
        navigate("/"); // Redirige a la página de inicio ("/")
    };

    return (
        <header className="bg-dark text-white p-4 d-flex justify-content-center align-items-center w-100 position-fixed top-0 start-0 z-10">
            
            <h1 className="fw-bold text-center m-0" style={{ cursor: "pointer" }} 
                onClick={handleHomeClick}>
                Gestor de productos
            </h1>
        </header>
    );
};

export default Header;
